import React, { useState, useEffect } from 'react';
import { SafeAreaView, Text, StyleSheet, StatusBar, View, TouchableHighlight, ScrollView } from 'react-native';
import Auth from './src/components/Auth/Auth';
import firebase from './Firebase/Firebase';
import HomeUser from './src/components/Home/LogUser/HomeUser';

export default function App() {
  const [user, setUser] = useState(undefined);

  useEffect(() => {
    firebase.auth().onAuthStateChanged((response) => {
      setUser(response);
    });
  }, []);
  if (user === undefined) return null;

  return (
    <>
      <StatusBar barStyle='light-content' />
      <SafeAreaView style={styles.background}>
        <ScrollView>
          {user ? <HomeUser /> : <Auth />}
          <Text style={styles.smartApps}>
            ©2020 Powered by Smartapps
            </Text>
        </ScrollView>
      </SafeAreaView>
    </>
  );
}

function Logout() {
  const logout = () => {
    firebase.auth().signOut();
  };

  return (
    <View>
      <Text>Estas Logeado</Text>
      <TouchableHighlight onPress={logout} style={styles.button}><Text style={styles.text}>Cerrar Sesión</Text></TouchableHighlight>
    </View>
  )
}

const styles = StyleSheet.create({
  background: {
    backgroundColor: '#03255F',
    height: '100%',
    alignItems: 'center',
    justifyContent: 'center',
  },
  button: {
    width: 300,
    height: 50,
    backgroundColor: '#F5C300',
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 10,
    marginBottom: 10,
    borderRadius: 8,
    borderWidth: 1,
  },
  smartApps: {
    fontSize: 10,
    color: 'white',
    fontWeight: 'bold',
    marginTop: 50,
  },
});
