import React, { useState, useEffect } from "react";
import {
	View,
	Text,
	TouchableOpacity,
	StyleSheet,
	Dimensions,
} from "react-native";
import HeaderView from "../../components/Layouts/Header";
import usePreference from "../../Hooks/usePreferences";
import firebase from "../../../Firebase/Firebase";
import moment from "moment";
import { useNavigation } from "@react-navigation/native";
import { validateEmail } from "../../Utils/validation";
import { Input, Icon, CheckBox } from "react-native-elements";
import { SafeAreaView } from "react-native-safe-area-context";
import Loading from "../../Utils/Loading";
import axios from "axios";
import AlcanciaImage from "../../components/Layouts/AlcanciaImage";

export default function DonarCompany(props) {
	const { toastRef } = props;
	const [loading, setLoading] = useState(false);
	const navigation = useNavigation();
	const { userFbData } = usePreference();
	const [formError, setFormError] = useState({});
	const [email, setEmail] = useState(userFbData.email);
	const [telefono, setTelefono] = useState(userFbData.telefono);
	const [representante, setRepresentante] = useState(userFbData.representante);
	const [aporte, setAporte] = useState();
	const [checked, setChecked] = useState(false);
	const [transbank, setTransbank] = useState(null);
	const [numeroOrden, setNumeroOrden] = useState();

	useEffect(() => {
		firebase
			.database()
			.ref("Transbank")
			.orderByChild("numero")
			.limitToLast(1)
			.on("value", (snapshot) => {
				setNumeroOrden(snapshot.val());
				console.log(numeroOrden);
			});
	}, []);

	const submit = () => {
		let orderToArray = [];
		let errors = [];
		if (!aporte || !email.trim() || !telefono || !representante) {
			toastRef.current.show("Todos Los Campos Son Obligatorios.");
			if (!aporte) errors.aporte = true;
			if (!email.trim()) errors.email = true;
			if (!telefono) errors.telefono = true;
			if (!representante) errors.representante = true;
		} else if (!validateEmail(email.trim())) {
			toastRef.current.show("Correo electr√≥nico incorrecto.");
			errors.email = true;
		} else {
			setLoading(true);
			firebase
				.database()
				.ref("Transbank")
				.orderByChild("numero")
				.limitToLast(1)
				.on("value", (snapshot) => {
					setNumeroOrden(snapshot.val());
				});
			if (numeroOrden) {
				console.log(numeroOrden);
				Object.keys(numeroOrden).forEach((key, i) => {
					orderToArray[i] = numeroOrden[key];
				});
				let key = parseInt(orderToArray[0].numero_orden.split('-')[1]) + 1;
				console.log(key);
				firebase
					.database()
					.ref()
					.child(`Transbank/orden_${key}`)
					.set({
						item: "Aporte",
						tipo: "Empresa",
						aporte: aporte,
						nombre: userFbData.nombre,
						rutEmpresa: userFbData.rutEmpresa,
						email: email,
						telefono: telefono,
						representante: representante,
						fecha: moment().format("DD-MM-YYYY h:mm:ss a"),
						numero_orden:  'JMAGALLANICAS-' + key,
						numero: key,
						estado_de_pago: "Pendiente",
						uid: userFbData.uid,
						certificado: checked,
						plataforma: "App",
					});
				firebase
					.database()
					.ref()
					.child(`Users/${userFbData.uid}/aportes/${key}/`)
					.set({
						aporte: aporte,
						nombre: userFbData.nombre,
						rutEmpresa: userFbData.rutEmpresa,
						email: email,
						telefono: telefono,
						representante: representante,
						fecha: moment().format("DD-MM-YYYY h:mm:ss a"),
						numero_orden:  'JMAGALLANICAS-' + key,
						estado_de_pago: "Pendiente",
						uid: userFbData.uid,
						certificado: checked,
					});
				firebase
					.database()
					.ref()
					.child(`Donaciones/${key}/`)
					.set({
						tipo: "Empresa",
						aporte: aporte,
						nombre: userFbData.nombre,
						rutEmpresa: userFbData.rutEmpresa,
						email: email,
						telefono: telefono,
						representante: representante,
						fecha: moment().format("DD-MM-YYYY h:mm:ss a"),
						numero_orden:  'JMAGALLANICAS-' + key,
						estado_de_pago: "Pendiente",
						uid: userFbData.uid,
						certificado: checked,
						plataforma: "App",
					})
					.then((res) => {
						axios({
							method: "post",
							url: "https://appjornadasmagallanicas.cl/api/api/transactions",
							data: {
								orden_compra:  'JMAGALLANICAS-' + key,
								sessionID: "DonacionApp",
								monto: aporte,
								cantidad: 1,
								nombre: representante,
								apellido: representante,
								email: userFbData.email,
								uid: userFbData.uid,
							},
						}).then((response) => {
							setTransbank(response.data);
							console.log(transbank);
							navigation.navigate("Pago Aporte", { transbank: response.data, orden_de_compra: key });
							setLoading(false);
						});
						handleReset();
					})
					.catch((err) => {
						setLoading(false);
						toastRef.current.show(
							"Ha ocurrido un problema, intente nuevamente."
						);
					});
			} else {
				setLoading(false);
				console.log("CATCH2");
