import React, { useState } from "react";
import {
	View,
	Text,
	Image,
	TouchableOpacity,
	StyleSheet,
	SafeAreaView,
	Modal,
	Linking,
	Dimensions,
	ImageBackground,
} from "react-native";
import { useNavigation } from "@react-navigation/native";
import Icon from "react-native-vector-icons/FontAwesome5";
import HeaderView from "../../components/Layouts/Header";
import firebase from "../../../Firebase/Firebase";
import usePreference from "../../Hooks/usePreferences";
import CarouselHome from "../../components/Layouts/Carousel";

export default function HomeCompany(props) {
	const navigation = useNavigation();
	const { userFbData, setUserFbData } = usePreference();
	const [modalVisible, setModalVisible] = useState(false);

	const Logout = () => {
		firebase.auth().signOut();
		setUserFbData(null);
	};

	if (userFbData) {
		return (
			<SafeAreaView style={styles.container}>
				<View style={styles.headerContainer}>
					<HeaderView props={props} />
				</View>
				<View style={styles.carouselContainer}>
					<CarouselHome />
				</View>
				<ImageBackground
					source={require("../../../assets/Emb.png")}
					style={styles.imageBackground}
					imageStyle={{
						resizeMode: "contain",
						opacity: 0.85,
					}}
					style={{ width: width, height: height * 0.6 }}
				>
					<View style={styles.buttonsContainer1}>
						<TouchableOpacity style={styles.buttonYellow}>
							<Text style={styles.textYellow}></Text>
						</TouchableOpacity>
						<TouchableOpacity
							// onPress={() => navigation.navigate("Donar")}
							style={styles.buttonBlue}
						>
							{/* <Text style={styles.textBlue}>
								<Icon name="donate" size={20} color="#F5C300" />
								{`\nAlcancía\nDigital`}
							</Text> */}
						</TouchableOpacity>
						<TouchableOpacity
							style={styles.buttonYellow}
							//onPress={() => navigation.navigate("Bono")}
						>
							<Text style={styles.textYellow}></Text>
						</TouchableOpacity>
					</View>
					<View style={styles.buttonsContainer2}>
						<TouchableOpacity
							onPress={() => navigation.navigate("Mis Donaciones")}
							style={styles.buttonBlue}
						>
							<Text style={styles.textBlue}>
								<Icon name="hand-holding-usd" size={20} color="#F5C300" />
								{`\nMis\nDonaciones`}
							</Text>
						</TouchableOpacity>
						<TouchableOpacity
							onPress={() => setModalVisible(true)}
							style={styles.buttonBlue}
						>
							<Image
								source={require("../../../assets/Cruz_de_malta.png")}
								style={{
									resizeMode: "stretch",
									width: width * 0.2,
									height: height * 0.106,
								}}
							/>
						</TouchableOpacity>
						<TouchableOpacity
							onPress={() => navigation.navigate("Perfil")}
							style={styles.buttonBlue}
						>
							<Text style={styles.textBlue}>
								<Icon name="users" size={20} color="#F5C300" />
								{`\nPerfil`}
							</Text>
						</TouchableOpacity>
					</View>
					<View style={styles.buttonsContainer3}>
						<TouchableOpacity style={styles.buttonYellow}>
							<Text style={styles.textYellow}></Text>
						</TouchableOpacity>
						<TouchableOpacity onPress={Logout} style={styles.buttonBlue}>
							<Text style={styles.textBlue}>
								<Icon name="door-open" size={20} color="#f5c300" />
								{`\nCerrar\nSesión`}
							</Text>
						</TouchableOpacity>
						{userFbData.subtipo ? (
							<TouchableOpacity
								onPress={() => navigation.navigate("Panel De Control")}
								style={styles.buttonYellow}
							>
								<Text style={styles.textYellow}>
									<Icon name="tools" size={20} color="#03255F" />
									{`\nPanel\nDe Control`}
								</Text>
							</TouchableOpacity>
						) : (
							<TouchableOpacity style={styles.buttonYellow}>
								<Text></Text>
							</TouchableOpacity>
						)}
					</View>
				</ImageBackground>
				<View style={styles.redesContainer}>
					{/* <Text style={styles.titleRedes}>Nuestras Redes Sociales</Text> */}
					<Icon
						name="facebook"
						size={35}
						color="#3b5998"
						style={styles.socialIcon}
						onPress={() =>
							Linking.openURL("https://www.facebook.com/lasjornadas/")
						}
					/>
					<Icon
						name="instagram"
						size={35}
						color="#c13584"
						style={styles.socialIcon}
						onPress={() =>
							Linking.openURL(
								"https://www.instagram.com/jornadasporlarehabilitacion/?hl=es"
							)
						}
					/>
					<Icon
						name="twitter"
						size={35}
						color="#00acee"
						style={styles.socialIcon}
						onPress={() =>
							Linking.openURL("https://twitter.com/lasjornadas?lang=es")
						}
					/>
					<Icon
						name="youtube"
						size={35}
						color="#c4302b"
						style={styles.socialIcon}
						onPress={() =>
							Linking.openURL("https://www.youtube.com/user/lasjornadas")
						}
					/>
				</View>
				<Modal
					animationType="slide"
					transparent={true}
					visible={modalVisible}
					onRequestClose={() => {
						Alert.alert("Modal has been closed.");
					}}
				>
					<View style={styles.centeredView}>
						<View style={styles.modalView}>
							<View style={styles.modalContent}>
								<ImageBackground
									source={require("../../../assets/banner-01.jpg")}
									onPress={() => Linking.openURL("http://jornadas.cl")}
									style={{
										resizeMode: "stretch",
										width: width * 0.84,
										height: 120,
										borderTopRightRadius: 18,
										borderTopLeftRadius: 18,
										marginTop: -25,
										overflow: "hidden",
										zIndex: -1,
									}}
								/>
								<Text style={styles.modalText1}>
									Jornadas Magallanicas App.
								</Text>
								<Text style={styles.modalText2}>
									© Copyrights 2020 Leones Cruz
								</Text>
								<Text style={styles.modalText25}>
									Sur all rights reserved.
								</Text>
								<Text style={styles.modalText3}>
									Hecho con 🧡 por{"   "}
									<Text
										onPress={() => Linking.openURL("http://smartapps.cl")}
										style={styles.smartApps}
									>
										SmartApps Spa.
									</Text>
								</Text>
								<TouchableOpacity
									style={styles.openButton}
									onPress={() => {
										setModalVisible(!modalVisible);
									}}
								>
									<Text style={styles.textStyle}>Cerrar</Text>
								</TouchableOpacity>
							</View>
						</View>
					</View>
				</Modal>
			</SafeAreaView>
		);
	} else {
		return null;
	}
}

