import React, { useState, useEffect } from "react";
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  SafeAreaView,
  ScrollView
} from "react-native";
import usePreference from "../../../Hooks/usePreferences";
import firebase from "../../../../Firebase/Firebase";
import Header from "../../../components/Layouts/Header";
import { useNavigation } from "@react-navigation/native";
import Icon from "react-native-vector-icons/FontAwesome5";

export default function Alcancias() {
  const navigation = useNavigation();
  const { userFbData } = usePreference();
  const [talonarios, setTalonarios] = useState([]);
  let talonariosToArray = [];

  useEffect(() => {
    firebase
      .database()
      .ref(`Users/${userFbData.uid}/talonarios`)
      .on("value", (snapshot) => {
        setTalonarios(snapshot.val());
      });
  }, []);

  if (talonarios && userFbData) {
    Object.keys(talonarios).forEach((key, i) => {
      talonariosToArray[i] = talonarios[key];
    });
    console.log(talonariosToArray);
    return (
		<>
		  <View style={styles.box1}>
		  <Header style={{flex: 1}} />
		  </View>
		  <View style={styles.box2}>
		  <Text style={styles.infoTitle}>Mis talonarios</Text>
		  </View>
		  <View>
				 <Text style={styles.infoTitle}>Mis talonarios</Text>
        <ScrollView>
          {talonariosToArray.map((talonario, i) => {
            return (
              <View key={i} style={styles.infoView}>
                <View style={styles.textBox}>
                  <Text style={styles.textKey}>Numero De talonario:</Text>
                  <Text style={styles.textValue}>
                    {talonario.talonario_numero}
                  </Text>
                </View>
                <View style={styles.textBox}>
                  <Text style={styles.textKey}>Correlativo:</Text>
                  <Text style={styles.textValue}>{talonario.correlativo}</Text>
                </View>
                {talonario.asignado_tercero === true ||
                talonario.asignado_externo === true ? (
                  <View style={styles.textBox}>
                    <Text style={styles.textKey}>Retirado: </Text>
                    {talonario.recuperado === true ? (
                      <Text style={[styles.textValue, { color: "green" }]}>
                        Si
                      </Text>
                    ) : (
                      <Text style={[styles.textValue, { color: "red" }]}>
                        No
                      </Text>
                    )}
                    <Text style={styles.textKey}>Entregado: </Text>
                    <Text style={[styles.textValue, { color: "green" }]}>
                      si
                    </Text>
                  </View>
                ) : (
                  <View style={styles.textBox}>
                    <Text style={styles.textKey}>Entregado: </Text>
                    <Text style={[styles.textValue, { color: "red" }]}>No</Text>
                  </View>
                )}
                <TouchableOpacity
                  onPress={() =>
                    navigation.navigate("InformacionTalonario", {
                      content: talonario,
                      uid: userFbData.uid,
                      key: i,
                      subtipo: userFbData.subtipo,
                    })
                  }
                  key={i}
                  style={styles.backButton}
                >
                  {talonario.asignado_tercero === true ||
                  talonario.asignado_externo === true ? (
                    <Icon
                      type="FontAwesome5"
                      name={
                        talonario.recuperado === true ? "check-circle" : "user"
                      }
                      size={50}
                      color={
                        talonario.recuperado === true ? "green" : "#34495E"
                      }
                    />
                  ) : (
                    <Icon
                      type="FontAwesome5"
                      name="question-circle"
                      size={50}
                      color="red"
                    />
                  )}
                </TouchableOpacity>
              </View>
            );
          })}
        </ScrollView>
		  </View>
		</>
    );
  } else {
    return (
      <SafeAreaView style={styles.mainView}>
        <Header />
        <Text style={styles.infoTitle}>Mis talonarios</Text>
        <ScrollView>
          <View style={{ alignItems: "center", marginTop: 30 }}>
            <Text
              style={{ fontWeight: "bold", color: "#03255F", fontSize: 15 }}
            >
              No tiene Talonarios asignados.
            </Text>
          </View>
        </ScrollView>
      </SafeAreaView>
    );
  }
}

const styles = StyleSheet.create({
	box1:{
		flex: 1,
	},
	box2:{
		flex:1,
	},
	box3:{
		flex: 4,
	},
  infoTitle: {
    marginVertical: 10,
    marginHorizontal: 10,
    fontWeight: "bold",
    color: "#fff",
    fontSize: 20,
    textAlign: "center",
    justifyContent: "center",
    backgroundColor: "#34495E",
    height: 30,
    borderRadius: 15,
    overflow: "hidden",
  },
  infoView: {
    paddingVertical: 10,
    marginVertical: 10,
    marginHorizontal: 26,
    backgroundColor: "#fff",
    borderWidth: 0.05,
    borderRadius: 5,
  },
  textBox: { flexDirection: "row" },
  textKey: {
    flex: 1,
    marginLeft: 3,
    fontSize: 15,
    fontWeight: "bold",
    color: "#03255f",
  },
  textValue: {
    fontSize: 15,
    fontWeight: "700",
    color: "#696969",
    marginRight: 70,
  },
  backButton: {
    flexDirection: "row",
    width: 55,
    height: 55,
    alignSelf: "flex-end",
    justifyContent: "center",
    borderRadius: 25,
    marginTop: 15,
    marginLeft: 20,
    position: "absolute",
  },
});
