import React, { useState } from "react";
import { WebView } from "react-native-webview";
import { View, TouchableOpacity, StyleSheet, SafeAreaView } from "react-native";
import { Icon } from "react-native-elements";
import { useNavigation } from "@react-navigation/native";
import HeaderView from "../../components/Layouts/Header";
import Loading from "../../Utils/Loading";

export default function PagoBono(props) {
  const navigation = useNavigation();
  const transbank = props.route.params.transbank;
  const orden_de_compra = props.route.params.orden_de_compra;
  const [loading, setLoading] = useState(false);

  console.log(orden_de_compra)
  const handleWebViewNavigationStateChange = (newNavState) => {
    const { url } = newNavState;
    if (!url) return;

    if (url.includes("voucher")) {
      setLoading(true);
      setTimeout(() => {
        setLoading(false);
        navigation.navigate("Exito Bono", {orden_de_compra: orden_de_compra});
      }, 10000);
    } else if (url.includes("fallido")) {
      setLoading(true);
      setTimeout(() => {
        setLoading(false);
        navigation.navigate("Fallo Bono", {orden_de_compra: orden_de_compra});
      }, 3000);
    }
  };

  if (transbank === null) {
    return (
      <SafeAreaView style={styles.mainView}>
        <HeaderView props={props} />
        <WebView
          source={{ uri: "https://appjornadasmagallanicas.cl/api/api/retorno" }}
          onNavigationStateChange={handleWebViewNavigationStateChange}
        />
        <View style={styles.backContainer}>
          <TouchableOpacity
            onPress={() => navigation.navigate("Home")}
            style={styles.comeBack}
          >
            <Icon
              raised
              name="arrow-left"
              type="font-awesome"
              color="#03255F"
              size={15}
            />
          </TouchableOpacity>
        </View>
      </SafeAreaView>
    );
  } else {
    return (
      <SafeAreaView style={styles.mainView}>
        <View style={styles.HeaderContainer}>
          <HeaderView props={props} />
        </View>
        <View style={styles.webviewContainer}>
          <WebView
            source={{
              uri: `${transbank.url}?token_ws=${transbank.token_ws}`,
            }}
            onNavigationStateChange={handleWebViewNavigationStateChange}
          />
        </View>
        <Loading isVisible={loading} text="Procesando pago..." />
      </SafeAreaView>
    );
  }
}

const styles = StyleSheet.create({
  mainView: {
    flex: 12,
  },
  HeaderContainer: {
    flex: 1,
  },
  webviewContainer: {
    flex: 10,
  },
  backContainer: {
    flex: 1,
    backgroundColor: "#03255f",
  },
  comeBack: {
    alignSelf: "center",
  },
});
