import React, { useState, useEffect } from 'react';
import { SafeAreaView, Text, StyleSheet, StatusBar, ScrollView } from 'react-native';
import { Provider as PaperProvider, DarkTheme as DarkThemePaper } from 'react-native-paper';
import Auth from './src/components/Auth/Auth';
import firebase from './Firebase/Firebase';
import Navigation from './src/components/Navigation/Navigation';
import { NavigationContainer, DarkTheme as DarkThemeNavigation } from '@react-navigation/native';

export default function App() {
  const [user, setUser] = useState(undefined);

  useEffect(() => {
    firebase.auth().onAuthStateChanged((response) => {
      setUser(response);
    });
  }, []);

  if (user === undefined) return null;
  DarkThemePaper.colors.primary = '#03255F';
  DarkThemePaper.colors.accent = 'grey';
  DarkThemeNavigation.colors.background = 'white';
  DarkThemeNavigation.colors.card = '#03255F';

  return (
    <>
      {user ?
        <PaperProvider theme={DarkThemePaper} >
          <NavigationContainer theme={DarkThemeNavigation}>
            <StatusBar barStyle={'dark-content'} />
            <Navigation />
          </NavigationContainer>
        </PaperProvider>
        :
        <>
          <StatusBar barStyle='light-content' />
          <SafeAreaView style={styles.background}>
            <ScrollView>
              <Auth />
              <Text style={styles.smartApps}>
                Â©2020 Powered by Smartapps
            </Text>
            </ScrollView>
          </SafeAreaView>
        </>
      }
    </>
  );
}

const styles = StyleSheet.create({
  background: {
    backgroundColor: '#03255F',
    height: '100%',
    alignItems: 'center',
    justifyContent: 'center',
  },
  button: {
    width: 300,
    height: 50,
    backgroundColor: '#F5C300',
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 10,
    marginBottom: 10,
    borderRadius: 8,
    borderWidth: 1,
  },
  smartApps: {
    fontSize: 10,
    color: 'white',
    fontWeight: 'bold',
    marginTop: 50,
  },
});
