import React, { useState, useEffect } from "react";
import {
	SafeAreaView,
	View,
	Text,
	StyleSheet,
	TouchableOpacity,
	Dimensions,
} from "react-native";
import HeaderView from "../../components/Layouts/Header";
import { numberFormat } from "../../Sources/PagoEnLinea/FormatPrice";
import usePreference from "../../Hooks/usePreferences";
import { validateEmail } from "../../Utils/validation";
import { useNavigation } from "@react-navigation/native";
import firebase from "../../../Firebase/Firebase";
import moment from "moment";
import { Input, Icon } from "react-native-elements";
import BonoImage from "../../components/Layouts/BonoImage";
import Loading from "../../Utils/Loading";
import axios from "axios";
import {validarRUT} from "validar-rut"

export default function BonoUser(props) {
	const { toastRef } = props;
	const [loading, setLoading] = useState(false);
	const navigation = useNavigation();
	const { userFbData } = usePreference();
	const [formError, setFormError] = useState({});
	const [cantidad, setCantidad] = useState(1);
	const [nombre, setNombre] = useState(userFbData.nombre);
	const [apellido, setApellido] = useState(userFbData.apellido);
	const [telefono, setTelefono] = useState(userFbData.telefono);
	const [rut, setRut] = useState(undefined);
	const [transbank, setTransbank] = useState(null);
	const [numeroOrden, setNumeroOrden] = useState();
	const precio = 1000;
	const precioTotal = precio * cantidad;

	useEffect(() => {
		firebase
			.database()
			.ref("Transbank")
			.orderByChild("numero")
			.limitToLast(1)
			.on("value", (snapshot) => {
				setNumeroOrden(snapshot.val());
				console.log(numeroOrden);
			});
	}, []);

	const handleCantidad = (cantidad, max) => {
		if (cantidad >= 1) {
			setCantidad(cantidad);
		} else if (cantidad < 0) {
			cantidad = 1;
			setCantidad(cantidad);
		} else if (cantidad >= max) {
			cantidad = max;
			setCantidad(cantidad);
		}
	};

	const comprar = () => {
		let orderToArray = [];
		let errors = {};
		if (!nombre || !apellido || !telefono || !rut) {
			toastRef.current.show("Todos Los Campos Son Obligatorios.");
			if (!nombre) errors.nombre = true;
			if (!apellido) errors.apellido = true;
			if (!telefono) errors.telefono = true;
			if (!rut) errors.rut = true;
		}else if (validarRUT(rut) === false){
			console.log(validarRUT(rut))
			toastRef.current.show("Formato de Rut incorrecto.");
			errors.rut = true;
		}else {
			setLoading(true);
			firebase
				.database()
				.ref("Transbank")
				.orderByChild("numero")
				.limitToLast(1)
				.on("value", (snapshot) => {
					setNumeroOrden(snapshot.val());
				});
			if (numeroOrden) {
				console.log(numeroOrden);
				Object.keys(numeroOrden).forEach((key, i) => {
					orderToArray[i] = numeroOrden[key];
				});
				let key = parseInt(orderToArray[0].numero_orden.split('-')[1]) + 1;
				console.log(key);
				firebase
					.database()
					.ref()
					.child(`Transbank/orden_${key}`)
					.set({
						item: "Bono",
						cantidad: cantidad,
						tipo: "Usuario",
						precio: precioTotal,
						nombre: nombre,
						apellido: apellido,
						rut: rut,
						telefono: telefono,
						fecha: moment().format("DD-MM-YYYY h:mm:ss a"),
						numero_orden:  'JMAGALLANICAS-' + key,
						numero: key,
						estado_de_pago: "Pendiente",
						uid: userFbData.uid,
						plataforma: "App",
					});
				firebase
					.database()
					.ref()
					.child(`Users/${userFbData.uid}/bonos/${key}/`)
					.set({
						total: precioTotal,
						nombre: nombre,
						apellido: apellido,
						rut: rut,
						telefono: telefono,
						cantidad: cantidad,
						numero_orden:  'JMAGALLANICAS-' + key,
						fecha: moment().format("DD-MM-YYYY h:mm:ss a"),
						id: key,
						estado_de_pago: "Pendiente",
						uid: userFbData.uid,
					});
				firebase
					.database()
					.ref()
					.child(`Bono_digital/${key}/`)
					.set({
						tipo: "Usuario",
						total: precioTotal,
						nombre: nombre,
						apellido: apellido,
						rut: rut,
						telefono: telefono,
						cantidad: cantidad,
						numero_orden:  'JMAGALLANICAS-' + key,
						fecha: moment().format("DD-MM-YYYY h:mm:ss a"),
						id: key,
						estado_de_pago: "Pendiente",
						uid: userFbData.uid,
						plataforma: "App",
					})
					.then((res) => {
						axios({
							method: "post",
							url: "https://appjornadasmagallanicas.cl/api/api/transactions",
							data: {
								orden_compra:  'JMAGALLANICAS-' + key,
								sessionID: "BonoSorteoApp",
								monto: precioTotal,
								cantidad: cantidad,
								nombre: nombre,
								apellido: apellido,
								uid: userFbData.uid,
								telefono: telefono,
							},
						}).then((response) => {
							setTransbank(response.data);
							console.log(transbank);
							navigation.navigate("Pago Bono", { transbank: response.data, orden_de_compra: key });
							setLoading(false);
						});
						handleReset();
					})
					.catch((err) => {
						setLoading(false);
						toastRef.current.show(
							"Ha ocurrido un problema, intente nuevamente."
						);
					});
			} else {
				setLoading(false);
				toastRef.current.show("Ha ocurrido un problema, intente nuevamente.");
			}
		}
		setFormError(errors);
	};

	const handleReset = () => {
		setCantidad(1);
		setNombre(userFbData.nombre);
		setApellido(userFbData.apellido);
		setEmail(userFbData.email);
		setTelefono(userFbData.telefono);
	};

	if (userFbData) {
		return (
			<SafeAreaView style={styles.container}>
				<View style={styles.headerContainer}>
					<HeaderView props={props} />
				</View>
				<View style={styles.imageContainer}>
					<BonoImage />
				</View>
				<View style={styles.titleContainer}>
					<Text style={styles.title}>Bono Sorteo Parcela</Text>
				</View>
				<View style={styles.inputContainer}>
					<Text style={styles.inputTitle}>Nombre(s)</Text>
					<Input
						name="nombre"
						containerStyle={styles.input}
						inputStyle={styles.inputText}
						inputContainerStyle={styles.inputUnderContainer}
						autoCapitalize="none"
						textContentType="name"
						placeholder="Fran... "
						defaultValue={nombre}
						rightIcon={
							formError.nombre ? (
								<Icon
									type="font-awesome"
									name="exclamation-circle"
									color="red"
								/>
							) : (
								<Icon
									type="Fontawesome5"
									name="edit"
									iconStyle={styles.iconRight}
								/>
							)
						}
						onChange={(e) => setNombre(e.nativeEvent.text)}
					/>
					<Text style={styles.inputTitle}>Apellido(s)</Text>
					<Input
						name="apellido"
						containerStyle={styles.input}
						inputStyle={styles.inputText}
						inputContainerStyle={styles.inputUnderContainer}
						autoCapitalize="none"
						textContentType="middleName"
						placeholder="Zun... "
						defaultValue={apellido}
						rightIcon={
							formError.apellido ? (
								<Icon
									type="font-awesome"
									name="exclamation-circle"
									color="red"
								/>
							) : (
								<Icon
									type="font-awesome-5"
									name="edit"
									iconStyle={styles.iconRight}
								/>
							)
						}
						onChange={(e) => setApellido(e.nativeEvent.text)}
					/>
					<Text style={styles.inputTitle}>Rut</Text>
					<Input
						name="apellido"
						containerStyle={styles.input}
						inputStyle={styles.inputText}
						inputContainerStyle={styles.inputUnderContainer}
						autoCapitalize="none"
						textContentType="postalCode"
						placeholder="11.111.111-1 "
						defaultValue={rut}
						rightIcon={
							formError.rut ? (
								<Icon
									type="font-awesome"
									name="exclamation-circle"
									color="red"
								/>
							) : (
								<Icon
									type="font-awesome-5"
									name="edit"
									iconStyle={styles.iconRight}
								/>
							)
						}
						onChange={(e) => setRut(e.nativeEvent.text)}
					/>
					<Text style={styles.inputTitle}>Telfono de Contacto</Text>
					<Input
						name="telefono"
						containerStyle={styles.input}
						inputStyle={styles.inputText}
						inputContainerStyle={styles.inputUnderContainer}
						autoCapitalize="none"
						placeholder="+56 9 1111 1111..."
						defaultValue={telefono}
						keyboardType="phone-pad"
						keyboardAppearance="dark"
						onChange={(e) => setTelefono(e.nativeEvent.text)}
						rightIcon={
							formError.telefono ? (
								<Icon
									type="font-awesome"
									name="exclamation-circle"
									color="red"
								/>
							) : (
								<Icon
									type="Fontawesome5"
									name="edit"
									iconStyle={styles.iconRight}
								/>
							)
						}
					/>
				</View>
				<TouchableOpacity
					onPress={() => handleReset()}
					style={styles.buttonFormReset}
				>
					<Text style={styles.formReset}>Reiniciar Formulario</Text>
				</TouchableOpacity>
				<View style={styles.quantityContainer}>
					<Text style={styles.title}>Cantidad</Text>
					<View style={styles.quantity}>
						<TouchableOpacity onPress={() => handleCantidad(cantidad - 1)}>
							<Icon
								type="material-community"
								name="minus-circle"
								color="white"
								size={35}
								iconStyle={styles.less}
							/>
						</TouchableOpacity>
						<Text style={styles.numero}>{cantidad}</Text>
						<TouchableOpacity onPress={() => handleCantidad(cantidad + 1)}>
							<Icon
								type="material-community"
								name="plus-circle"
								color="white"
								size={35}
								iconStyle={styles.plus}
							/>
						</TouchableOpacity>
					</View>
					<TouchableOpacity
						onPress={() => setCantidad(1)}
						style={styles.buttonReset}
					>
						<Text style={styles.textReset}>Reiniciar</Text>
					</TouchableOpacity>
				</View>
				<View style={styles.submitContainer}>
					<TouchableOpacity onPress={comprar} style={styles.buttonPagar}>
						<Text style={styles.bonoSubmit}>
							{numberFormat(precioTotal)} Comprar
						</Text>
					</TouchableOpacity>
				</View>
				<View style={styles.backContainer}>
					<TouchableOpacity
						onPress={() => navigation.navigate("Home")}
						style={styles.comeBack}
					>
						<Icon
							raised
							name="arrow-left"
							type="font-awesome"
							color="#03255F"
						/>
					</TouchableOpacity>
				</View>
				<Loading isVisible={loading} text="Procesando pago" />
			</SafeAreaView>
		);
	}
}

