import React, { useState } from 'react';
import { StyleSheet } from 'react-native';
import { DrawerContentScrollView } from '@react-navigation/drawer';
import { Drawer, Switch, TouchableRipple, Text } from 'react-native-paper';

const DrawerContent = (props) => {
    const { navigation } = props;
    const [active, setActive] = useState("Home");

    const onChangeScreen = (screen) => {
        setActive(screen)
        navigation.navigate(screen);
    }
    const CustomDrawerComponent = (props) => (
        <SafeAreaView style={{ flex: 1, backgroundColor: "#03255F" }}>
            <Header style={styles.header}>
                <Icon style={styles.icon} name="menu" onPress={() => props.navigation.closeDrawer()} />
            </Header>
            <View Style={{ height: 50 }}>
                <Image
                    source={require("./assets/logo_jornadas.png")}
                    style={{
                        width: null,
                        resizeMode: "contain",
                        height: 220,
                        marginBottom: -50,
                        marginTop: -30,
                    }}
                />
            </View>
            <ScrollView style={{ backgroundColor: 'white' }}>
                <DrawerItems {...props} style={{ texttintColor: 'green' }} />
            </ScrollView>
        </SafeAreaView>
    );

    return (
        <DrawerContentScrollView>
            <Drawer.Section>
                <Drawer.Item label='Home' active={active === 'Home'} onPress={() => onChangeScreen('Home')} />
                <Drawer.Item label='Alcancia' active={active === 'Alcancia'} onPress={() => onChangeScreen('Alcancia')} />
                <Drawer.Item label='Bono' active={active === 'Bono'} onPress={() => onChangeScreen('Bono')} />
            </Drawer.Section>
        </DrawerContentScrollView>
    )
}

export default DrawerContent;

const styles = StyleSheet.create({})
